mapper.select:
select seq, writer, title, thumbnail, content, ingredients, cookingtime, serving , category, regdate, star, tag from ( select row_number()over(order by regdate desc) as rnum, seq, writer, title, thumbnail, content, ingredients, cookingtime, serving , category, regdate, star, tag from recipe where 1=1
and title like concat('%', #{search},'%')
and content like concat('%', #{search},'%')
and writer=#{search}
order by regdate desc ) a where rnum between ${start} and ${end}

select seq, writer, title, thumbnail, content, ingredients, cookingtime, serving , category, regdate, star, tag
from recipe
where seq=#{seq}

mapper.update:
update recipe
set thumbnail=#{thumbnail}, title=#{title}, content=#{content}, ingredients=#{ingredients}, cookingtime=#{cookingtime}, serving=#{serving}, category=#{category}, regdate=now(), tag=#{tag}
where seq=#{seq}

mapper.select:
select seq, content, writer
from reply
where seq=#{seq}
