mapper.select:
select userId, name
from user
where userId=#{userId} and password=#{password}

select count(*)
from user
where userId=#{id}

select *
from user
where userId=#{userId} and password=#{password}

select userId
from user
where email=#{email}

select password
from user
where email=#{email}

select r.seq, r.writer ,r.title, r.thumbnail, r.star, r.regdate, r.tag
from recipe r, user u
where u.userId = r.writer and u.userId = #{userId}
order by r.seq desc;
