mapper.select:
select user_id, name
from user
where user_id=#{user_id} and password=#{password}

select count(*)
from user
where user_id=#{id}

select user_id, password
from user
where user_id=#{user_id} and password=#{password}

select user_id
from user
where email=#{email}

select password
from user
where email=#{email}
